import React, { Component } from 'react';
import FlatList from 'flatlist-react';
import { makeStyles } from '@material-ui/core/styles';
import { Card } from '@material-ui/core/Card';
import { CardActions } from '@material-ui/core/CardActions';
import { CardContent } from '@material-ui/core/CardContent';
import { Button } from '@material-ui/core/Button';
import { Typography }from '@material-ui/core/Typography';
import { connect } from 'react-redux';
import { removeFromQueue, addToArchive } from '../redux/actions/movieController.js'
import { modalInteraction } from '../redux/actions/postMovieModalController.js';
import PostMovie from './PostMovie.js'

class MovieQueue extends Component {
  constructor (props) {
    super(props);
    this.state = {}
    this.togglePost = this.togglePost.bind(this);
  }

  togglePost(e) {
    e.preventDefault();
    this.props.modalInteraction(true);
  }

  renderMovie = (movie, i) => {
  return (
      <div key={i}>
        <span>{i + 1}</span>
        <span>{movie.title}</span>
        <span>{movie.link}</span>
        <span>{movie.genre}</span>
        <span>{movie.runtime}</span>
        <span>{movie.user}</span>
        <button onClick={() => this.props.removeFromQueue(i)}>remove</button>
        <button onClick={() => this.props.addToArchive(i)}>archive</button>
      </div>
  );
}

  render() {
    const classes = useStyles();

    return (
      <div className="movieQueueContain">
        <div className="movieQueueComponent">
          <button onClick={this.togglePost}>ADD</button>
          <FlatList
            list={this.props.queue}
            renderItem={this.renderMovie}
            renderWhenEmpty={() => <div>List is empty!</div>}
          />
        </div>
          {
            this.props.isPostMovieModalOpen
            ?
            <PostMovie />
            :
            null
          }
      </div>
    );
   }
}

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

function mapStateToProps(state) {
  return {
    queue: state.movieReducer.queue,
    isPostMovieModalOpen: state.postMovieModalReducer.isPostMovieModalOpen
  };
}

function mapDispatchToProps(dispatch) {
  return {
    removeFromQueue: index => dispatch(removeFromQueue(index)),
    addToArchive: index => dispatch(addToArchive(index)),
    modalInteraction: modalState => dispatch(modalInteraction(modalState))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MovieQueue);
