const initialState = {
  queue: [{title: "test", genre: "test", link: 'test', runtime: 'test', user: 'test'}],
  archive: [],
  marqueeFilm: [{title: "this is gonna be a really long line sick it works", genre: "ishh", link: 'pop', runtime: 'jakey', user: 'rivers'}]

}
export default function(state = initialState, action) {
  switch (action.type) {
    case "addToQueue":
      return {
        ...state,
        queue: state.queue.concat(action.payload)
      };
    case "removeFromQueue":
      return {
        ...state,
        queue: state.queue.filter((payload, index) => index !== action.payload)
      };
    case "addToMarquee":
    return {
      ...state,
      marqueeFilm: [
          ...state.queue.filter((payload, index) => index === action.payload)
      ],
      queue: state.queue.filter((payload, index) => index !== action.payload)
    };
    case "addToArchive":
    return {
      ...state,
      archive: [
          ...state.archive,
          ...state.marqueeFilm.filter((payload, index) => index === action.payload)
      ],
      marqueeFilm: []
    };
    default:
      return state;
  };
}
