import React, { Component } from 'react';
import FlatList from 'flatlist-react';
import { Card } from '@material-ui/core';
import { Button } from '@material-ui/core';
import { connect } from 'react-redux';
import { removeFromQueue, addToArchive } from '../redux/actions/movieController.js'
import { modalInteraction } from '../redux/actions/postMovieModalController.js';
import PostMovie from './PostMovie.js'

class MovieQueue extends Component {
  constructor (props) {
    super(props);
    this.state = {
      shouldRender: true
    }
    this.togglePost = this.togglePost.bind(this);
  }

  togglePost(e) {
    e.preventDefault();
    this.props.modalInteraction(true);
  }

  toggleRender() {
    if (this.props.isSideDrawerOpen) {
      this.setState({shouldRender: false})
    };
  }

  renderMovie = (movie, i) => {
  return (
    <div
      class='queueCardContain'
      style={{ animation: `${this.state.shouldRender ? "slideLeftStart" : "slideLeftEnd"} 0.4s cubic-bezier(.21,.07,.3,.97)` }}
    >
      <h4 className="queueCardNumber">{i + 1}.</h4>
      <Card className='movieObjectContain' key={i}>
        <h3 className='queueCardTitle'>" {movie.title} "</h3>
        <h4 className='queueCardGenre'>{movie.genre}</h4>
        <h4 className='queueCardRuntime'>Runtime: {movie.runtime}</h4>
        <a href={movie.link} className='queueCardLink'>View Trailer</a>
        <h4 className='queueCardUser'>Requested by {movie.user}</h4>
        <div className="queueButtonContain">
          <Button
            variant="contained"
            color='secondary'
            className='queueRemoveButton'
            onAnimationEnd={this.state.shouldRender ? null : () => this.props.removeFromQueue(i)}
            onClick={() => this.toggleRender()}
          >
            remove
          </Button>
          <Button variant="contained" color='primary' className='queueArchiveButton' onClick={() => this.props.addToArchive(i)}>archive</Button>
        </div>
      </Card>
    </div>
  );
}

  render() {
    return (
      <div className="movieQueueContain">
        <h2 className="listTitle">To Watch Queue</h2>
        <Button className="addToQueueButton" onClick={this.togglePost}>ADD</Button>
        <div className="movieCard">
          <FlatList
            list={this.props.queue}
            renderItem={this.renderMovie}
            renderWhenEmpty={() => <div>List is empty!</div>}
          />
        </div>
          {
            this.props.isPostMovieModalOpen
            ?
            <PostMovie />
            :
            null
          }
      </div>
    );
   }
}


function mapStateToProps(state) {
  return {
    queue: state.movieReducer.queue,
    isPostMovieModalOpen: state.postMovieModalReducer.isPostMovieModalOpen
  };
}

function mapDispatchToProps(dispatch) {
  return {
    removeFromQueue: index => dispatch(removeFromQueue(index)),
    addToArchive: index => dispatch(addToArchive(index)),
    modalInteraction: modalState => dispatch(modalInteraction(modalState))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MovieQueue);
