import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card } from '@material-ui/core';
import { Button } from '@material-ui/core';
import { addToArchive } from '../redux/actions/movieController.js'

class Marquee extends Component {

  render() {
    return (
      <div className="movieQueueContain">
        <h2 className="listTitle">To Watch Queue</h2>
        <Card className='movieObjectContain'>
          <h3 className='queueCardTitle'>" {this.props.marqueeFilm.i.title} "</h3>
          <h4 className='queueCardGenre'>{this.props.marqueeFilm.genre}</h4>
          <h4 className='queueCardRuntime'>Runtime: {this.props.marqueeFilm.runtime}</h4>
          <a href={this.props.marqueeFilm.link} className='queueCardLink'>View Trailer</a>
          <h4 className='queueCardUser'>Requested by {this.props.marqueeFilm.user}</h4>
        </Card>
        <Button variant="contained" color='primary' className='queueArchiveButton' onClick={() => this.props.addToArchive()}>archive</Button>
      </div>
    );
   }
}


function mapStateToProps(state) {
  return {
    marqueeFilm: state.movieReducer.marqueeFilm
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addToArchive: index => dispatch(addToArchive(index))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Marquee);
