import React from 'react';
import { connect } from 'react-redux';
import Marquee from './Marquee.js'
import MovieLists from './MovieQueue.js'
import { getMovieListYPos } from '../redux/actions/yPosController.js'
import '../../App.css';

class MovieScreen extends React.Component {
  constructor() {
    super()
    this.state = {
      shouldRender: true
    }
  }

  render() {
    return (
      <div>
        <Marquee />
        <MovieLists />
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    marqueeHeight: state.yPosReducer.marqueeHeight,
    navbarHeight: state.yPosReducer.navbarHeight,
    movieListYPos: state.yPosReducer.movieListYPos,
    isSideDrawerOpen: state.sideDrawerReducer.isSideDrawerOpen
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getMovieListYPos: index => dispatch(getMovieListYPos(index))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MovieScreen);
