import React, { Component } from 'react';
import { connect } from 'react-redux';
import FlatList from 'flatlist-react';
import { Card } from '@material-ui/core';
import { Button } from '@material-ui/core';
import { addToArchive } from '../redux/actions/movieController.js'

class Marquee extends Component {
  constructor (props) {
    super(props);
    this.state = {
      alternateLights: false
    }
    this.alternateLights = this.alternateLights.bind(this);
  }

  componentDidMount() {
    this.alternateLights()
  }

  alternateLights() {
    for (;;) {
      this.setInterval(this.setState({alternateLights: !this.state.alternateLights}), 1000)
    }
  }

  renderMovie = (movie, i) => {
  return (
    <div key={i}>
      <div className="marqueeContain">
        <div className="marqueeBorder"/>
        <div className="mlEmpty emp1" />
        <h2 className="nowPlaying">NOW PLAYING</h2>
        <div className="marqueeLine ml1" />
        <div className="marqueeLine ml2" />
        <div className="marqueeLine ml3" />
        <h2 className='marqueeFilmTitle'>" {movie.title} "</h2>
        <div className="mlEmpty emp2" />
      </div>
      <Card className='movieObjectContain'>
        <h3 className='queueCardTitle'>" {movie.title} "</h3>
        <h4 className='queueCardGenre'>{movie.genre}</h4>
        <h4 className='queueCardRuntime'>Runtime: {movie.runtime}</h4>
        <a href={movie.link} className='queueCardLink'>View Trailer</a>
        <h4 className='queueCardUser'>Requested by {movie.user}</h4>
      </Card>
      <Button variant="contained" color='primary' className='queueArchiveButton' onClick={() => this.props.addToArchive(i)}>archive</Button>
    </div>
  );
}

  render() {
    return (
      <div className="movieQueueContain">
        <FlatList
          list={this.props.marqueeFilm}
          renderItem={this.renderMovie}
          renderWhenEmpty={() => null}
        />
      </div>
    );
   }
}


function mapStateToProps(state) {
  return {
    marqueeFilm: state.movieReducer.marqueeFilm
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addToArchive: index => dispatch(addToArchive(index))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Marquee);
