import React  from 'react'
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'
import { sideDrawerAction } from './redux/actions/sideDrawerController.js'
import { ReactComponent as Cross } from '../assets/cross.svg';

class SideDrawer extends React.Component {
  constructor() {
    super()
    this.state = {
      shouldRender: true,
      setRender: false
    }
    this.toggleRender = this.toggleRender.bind(this);
    this.onAnimationEnd = this.onAnimationEnd.bind(this);
  }

  toggleRender() {
    if (this.props.isSideDrawerOpen) {
      this.setState({shouldRender: false})
    };
  }

  onAnimationEnd() {
    if (this.state.shouldRender = false) {
      this.props.sideDrawerAction(false);
      console.log(this.props.isSideDrawerOpen)
    }
  };

  render() {
    return (
        <div>
          {console.log(this.state.shouldRender)}
          <div className='drawerContain'>
            <Cross className='exitMenuIcon' onClick={() => this.props.sideDrawerAction(false)} />
            <div className='menuListMobile'>
              <Link onClick={() => this.props.sideDrawerAction(false)} to='/'>
                <h3>Movies</h3>
              </Link>
              <Link onClick={() => this.props.sideDrawerAction(false)} to='/about'>
                <h3>About</h3>
              </Link>
              <Link onClick={() => this.props.sideDrawerAction(false)} to='/support'>
                <h3>Support</h3>
              </Link>
            </div>
          </div>
          <div
            className='backdrop'
            style={{ animation: `${this.state.shouldRender ? "backdropOpacityStart" : "backdropOpacityEnd"} 0.4s cubic-bezier(.21,.07,.3,.97)` }}
            onAnimationEnd={() => this.onAnimationEnd}
            onClick={() => this.toggleRender()} />
        </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    isSideDrawerOpen: state.sideDrawerReducer.isSideDrawerOpen
  };
}

function mapDispatchToProps(dispatch) {
  return {
    sideDrawerAction: modalState => dispatch(sideDrawerAction(modalState))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(SideDrawer);
