import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card } from '@material-ui/core';
import { Button } from '@material-ui/core';
import { addToArchive } from '../redux/actions/movieController.js'

class Marquee extends Component {

  render() {
    return (
      <div className="movieQueueContain">
        <h2 className="listTitle">To Watch Queue</h2>
        <Card className='movieObjectContain' key={i}>
          <h3 className='queueCardTitle'>" {movie.title} "</h3>
          <h4 className='queueCardGenre'>{movie.genre}</h4>
          <h4 className='queueCardRuntime'>Runtime: {movie.runtime}</h4>
          <a href={movie.link} className='queueCardLink'>View Trailer</a>
          <h4 className='queueCardUser'>Requested by {movie.user}</h4>
        </Card>
        <Button variant="contained" color='primary' className='queueArchiveButton' onClick={() => this.props.addToArchive(i)}>archive</Button>
        <div className="movieCard">
        </div>
          {
            this.props.isPostMovieModalOpen
            ?
            <PostMovie />
            :
            null
          }
      </div>
    );
   }
}


function mapStateToProps(state) {
  return {
    marqueeFilm: state.movieReducer.marqueeFilm
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addToArchive: index => dispatch(addToArchive(index))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Marquee);
