import React from 'react';
import { connect } from 'react-redux';
import MovieQueue from './MovieQueue.js'
import ArchiveList from './ArchiveList.js'
import Marquee from './Marquee.js'
import { getMovieListYPos } from '../redux/actions/yPosController.js';
import '../../App.css';

class MovieScreen extends React.Component {
  constructor() {
    super()
    this.state = {
      shouldTransitionDown: false
    }
  }

  static getDerivedStateFromProps(props, state) {
    return {
      shouldTransitionDown: props.test
    };
  }

  render() {
    return (
      <div className="movieScreen">
      {console.log(this.props.marqueeHeight)}
        <Marquee />
        <div
          id='queueAndArchive'
          className="queueAndArchive"
          style={{ transform: `translateY(${this.props.marqueeHeight}px) !important` }}
        >
          <MovieQueue />
          <ArchiveList />
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    isModalOpen: state.postMovieModalReducer.isModalOpen,
    test: state.postMovieModalReducer.test,
    navbarHeight: state.yPosReducer.navbarHeight,
    marqueeHeight: state.yPosReducer.marqueeHeight,
    movieListYPos: state.yPosReducer.movieListYPos,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getMovieListYPos: movie => dispatch(getMovieListYPos(movie)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MovieScreen);
