import React from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom'
import { connect } from 'react-redux';
import NavBar from './components/Navbar.js'
import SideDrawer from './components/SideDrawer.js'
import MovieScreen from './components/movieScreen/MovieScreen.js'
import About from './components/About.js'
import Support from './components/Support.js'
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.state = {}
    this.handleDimensionChange = this.handleDimensionChange.bind(this);
  }

  componentDidMount() {
    this.handleDimensionChange();
    window.addEventListener("resize", this.handleDimensionChange);
  }

  handleDimensionChange() {
    this.setState({
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight
    });
  }

  render() {
    return (
      <Router>
        <div className="appBackground">
          <NavBar />
          {this.props.isSideDrawerOpen ? <SideDrawer /> : null}
          <Route exact path="/" component={MovieScreen} />
          <Route path="/about" component={About} />
          <Route path="/support" component={Support} />
        </div>
      </Router>
    );
  }
}

function mapStateToProps(state) {
  return {
    isSideDrawerOpen: state.sideDrawerReducer.isSideDrawerOpen
  };
}

export default connect(mapStateToProps, null)(App);
