import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addToQueue } from './redux/actions/movieController';
import { modalInteraction } from './redux/actions/postMovieModalController.js';


class PostMovie extends Component {
  constructor (props) {
    super(props);
    this.state = {
      title: "",
      link: "",
      genre: "",
      runtime: "",
      user: ""
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    e.preventDefault();
    this.setState({[e.target.name]: e.target.value});
  }

  handleSubmit(e) {
    e.preventDefault();
    this.props.addToQueue({
      title: this.state.title,
      link: this.state.link,
      genre: this.state.genre,
      runtime: this.state.runtime,
      user: this.state.user
    });
    this.setState({
      title: '',
      link: '',
      genre: '',
      runtime: '',
      user: ''
    });
    this.props.modalInteraction(false)
  }

  render() {
    return(
      <div class="postMovieBackground">
        <div class="postMovieModal">
          <h2 class="postMovieHeader">Post Film Suggestion</h2>
          <form class="movieForm" name="movieForm" onSubmit={this.handleSubmit}>
             <input required type="text" name="title" value={this.state.title} placeholder="Title" onChange={this.handleChange}/>
             <input required type="text" name="link" value={this.state.link} placeholder="Link" onChange={this.handleChange}/>
             <input required type="text" name="genre" value={this.state.genre} placeholder="Genre" onChange={this.handleChange}/>
             <input required type="text" name="runtime" value={this.state.runtime} placeholder="Runtime" onChange={this.handleChange}/>
             <input required type="text" name="user" value={this.state.user} placeholder="User" onChange={this.handleChange}/>
             <button>Post</button>
          </form>
        </div>
      </div>
    )
  }
}

function mapDispatchToProps(dispatch) {
  return {
    addToQueue: movie => dispatch(addToQueue(movie)),
    modalInteraction: modalState => dispatch(modalInteraction(modalState))
  };
}

export default connect(null, mapDispatchToProps)(PostMovie);
