import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { connect } from 'react-redux';
import { addToQueue } from '../redux/actions/movieController';
import { modalInteraction } from '../redux/actions/postMovieModalController.js';


class PostMovie extends Component {
  constructor (props) {
    super(props);
    this.state = {
      title: "",
      link: "",
      genre: "",
      runtime: "",
      user: ""
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    e.preventDefault();
    this.setState({[e.target.name]: e.target.value});
  }

  handleSubmit(e) {
    e.preventDefault();
    this.props.addToQueue({
      title: this.state.title,
      link: this.state.link,
      genre: this.state.genre,
      runtime: this.state.runtime,
      user: this.state.user
    });
    this.setState({
      title: '',
      link: '',
      genre: '',
      runtime: '',
      user: ''
    });
    this.props.modalInteraction(false)
  }

  render() {
    return(
      <div className="postMovieBackground">
        <div className="postMovieModal">
          <h3 className="postMovieHeader">Post Film Suggestion</h3>
          <form className="movieForm" name="movieForm" onSubmit={this.handleSubmit}>
             <TextField required className="postMovieInput" type="text" name="title" value={this.state.title} placeholder="Title" onChange={this.handleChange} variant="filled"/>
             <TextField required className="postMovieInput" type="text" name="link" value={this.state.link} placeholder="Link" onChange={this.handleChange} variant="filled"/>
             <TextField required className="postMovieInput" type="text" name="genre" value={this.state.genre} placeholder="Genre" onChange={this.handleChange} variant="filled"/>
             <TextField required className="postMovieInput" type="text" name="runtime" value={this.state.runtime} placeholder="Runtime" onChange={this.handleChange} variant="filled"/>
             <TextField required className="postMovieInput" type="text" name="user" value={this.state.user} placeholder="User" onChange={this.handleChange} variant="filled"/>
             <Button className="postButton">Post</Button>
          </form>
        </div>
        <div className='backdrop' onClick={() => this.props.modalInteraction(false)} />
      </div>
    )
  }
}

function mapDispatchToProps(dispatch) {
  return {
    addToQueue: movie => dispatch(addToQueue(movie)),
    modalInteraction: modalState => dispatch(modalInteraction(modalState))
  };
}

export default connect(null, mapDispatchToProps)(PostMovie);
