import React from 'react';
import { connect } from 'react-redux';
import MovieQueue from './MovieQueue.js'
import ArchiveList from './ArchiveList.js'
import Marquee from './Marquee.js'
import { getMovieListYPos } from '../redux/actions/yPosController.js'
import '../../App.css';

class MovieScreen extends React.Component {
  constructor() {
    super()
    this.state = {
      shouldRender: true
    }
  }

  render() {
    return (
      <div>
        <Marquee />
        <MovieLists />
      </div>
    );
  }
}

class MovieLists extends React.Component {
  constructor() {
    super()
    this.state = {}
    this.documentMovieListBounding = this.documentMovieListBounding.bind(this)
  }

  componentDidUpdate() {
    this.documentMovieListBounding()
  }

  documentMovieListBounding() {
    let el = document.getElementById('marquee');
    let elBounding = el.getBoundingClientRect();
    let top = elBounding.top;

    this.props.getMovieListYPos(top)
  }

  render() {
    return (
      <div id="movieLists" className="movieListsContain">
        <MovieQueue />
        <ArchiveList />
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    marqueeHeight: state.yPosReducer.marqueeHeight,
    navbarHeight: state.yPosReducer.navbarHeight,
    isSideDrawerOpen: state.sideDrawerReducer.isSideDrawerOpen
  };
}

export default connect(mapStateToProps, null)(MovieScreen);
