const initialState = {
  isMarqueeVisible: false
}
export default function(state = initialState, action) {
  switch (action.type) {
    case "checkMarqueeVisibility":
      return {
        ...state,
        isMarqueeVisible: action.payload
      };
    default:
      return state;
  }
}
