const initialState = {
  marqueeHeight: 0,
}
export default function(state = initialState, action) {
  switch (action.type) {
    case "getMarqueeHeight":
      return {
        ...state,
        marqueeHeight: action.payload
      };
    default:
      return state;
  }
}
